<!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1">    <title><?php echo Yii::t('app', '修改资料'); ?></title>    <link rel="stylesheet" href="/css/ymj.css" /></head><body><div class="am-container">    <div class="am-g">        <div class="am-u-sm-12 userGo">            <div class="accordion2">                <div class="accordionInput frm">                    <input type="email" name="eamil" id="email" value="<?php echo $user->userprofile->email?>" placeholder="<?php echo Yii::t('app', '请填写邮箱'); ?>" class="txt large fl" step="1">                    <label title="<?php echo Yii::t('app', '邮箱'); ?>"></label>                    <span role="tooltip"></span>                </div>                <div class="goRight opacity6 closeInput"><img src="/img/close_gray_18.png" /></div>            </div>            <div class="accordion2">                <div class="accordionInput frm">                    <input type="text" name="alipay" id="alipay" value="<?php echo $user->userprofile->alipay?>"  placeholder="<?php echo Yii::t('app', '请填写支付宝账号'); ?>" class="txt large fl" step="1">                    <label title="<?php echo Yii::t('app', '支付宝账号'); ?>"></label>                    <span role="tooltip"></span>                </div>                <div class="goRight opacity6 closeInput"><img src="/img/close_gray_18.png" /></div>            </div>            <div class="accordion2">                <div class="accordionInput frm">                    <input type="text" name="wechat" id="wechat"  value="<?php echo $user->userprofile->wechat?>"  placeholder="<?php echo Yii::t('app', '请填写微信号'); ?>" class="txt large fl" step="1">                    <label title="<?php echo Yii::t('app', '微信号'); ?>"></label>                    <span role="tooltip"></span>                </div>                <div class="goRight opacity6 closeInput"><img src="/img/close_gray_18.png" /></div>            </div>        </div>        <div class="am-u-sm-12 safeLogout">            <button type="button" class="btn" style="width: 80%; background-color:#1e88e5;color:white;"><?php echo Yii::t('app', '提交'); ?></button>        </div>    </div></div><script charset="utf-8" src="/js/3.2.1.js"></script><script charset="utf-8" src="/js/common.js"></script><script>    $(".btn").click(function(){        var email = $("#email").val(),            alipay = $("#alipay").val(),            wechat = $("#wechat").val();        if(email == '' && alipay == '' && wechat == ''){            alert('<?php echo Yii::t('app','请完善资料');?>');            return;        }        $(".btn").prop('disabled',true);        var url = "/user/update.html";        $.ajax({            type: "post",            data: {email:email, alipay:alipay, wechat:wechat},            dataType: "json",            url: url,            success: function (data) {                if (data.status == '0000') {                    alert(data.msg);                } else {                    alert(data.msg);                    $(".btn").prop('disabled',false);                }            }        });    })</script></body></html>